package msgconv

import (
	"encoding/hex"
	"github.com/stretchr/testify/assert"
	"github.com/stretchr/testify/require"
	"testing"
)

func TestEvmOp0(t *testing.T) {
	payload, err := EvmOp0("vault", []byte("0xf39Fd6e51aad88F6F4ce6aB8827279cffFb92266"), []byte("100000000000x5Cbb2F9f7E54c5B4656C3B563ff5650a0866A3EF"))
	require.Nil(t, err)
	assert.Equal(t, hex.EncodeToString(payload), "")
}
